<?php	try {			$conexion=include ("../db_connect.php");			$db = new DB_CONNECT();			 			$result = array();			$result["error"] = 0;			$result["descripcion"] = '';			$result["total"] = 10;			$result["rows"] = array();			$rows_reg = 10;			$query = ' ';			$flag=-1;			$cuit_emp = isset($_GET['cuit_emp']) ? $_GET['cuit_emp'] : $flag;			if($cuit_emp!=$flag){				$query = " SELECT SUC.nombre, CONCAT(DIR.calle,' ' ,DIR.altura) AS direccion, SUC.id FROM sucursal as SUC INNER JOIN direccion as DIR ON SUC.id_dir=DIR.id WHERE SUC.cuit_emp='$cuit_emp' ";				$rs = mysql_query(" select count(*) FROM sucursal as SUC INNER JOIN direccion as DIR ON SUC.id_dir=DIR.id WHERE SUC.cuit_emp='$cuit_emp'  ");			}						$row = mysql_fetch_row($rs);			$result["total"] = $row[0];			$rows_reg = $result["total"];			$rs = mysql_query($query) or die(mysql_error());			$rows = array();			while ($row = mysql_fetch_object($rs)) { //Recupera una fila del objeto $rs				array_push($rows, $row);			}			$result["rows"] = $rows;			//Busco posibles errores:			if ($result["total"] <= 0) {				$result["error"] = -1;				$result["descripcion"] = "Sucursales(s) no encontrada(s)";			}		} catch (exception $e) {			$result["error"] = -400;			$result["descripcion"] = "Error al ejecutar SQL";		}	echo json_encode($result);	?>